import os


def findMaxConsecutiveOnes(nums):

    if nums.count(1) == 0:
        return 1

    if nums.count(1) >= len(nums) - 1:
        return len(nums)

    last_max_1_num = -1
    if len(nums) < 2:
        return sum(s.count(1) for s in nums)
    for i in range(1, len(nums) - 1):
        if nums[i] == 0:
            m = i - 1
            n = i + 1
            while m >= 0:
                if nums[m] == 0:
                    m = m + 1
                    break
                m-= 1
            while n < len(nums):
                if nums[n] == 0:
                    n = n - 1
                    break
                n += 1

            if m == -1:
                m = 0
            if n == len(nums):
                n = len(nums) - 1

            total = len(nums[m:n + 1])
            if total > last_max_1_num:
                last_max_1_num = total
    return last_max_1_num


